<?php if( !defined( 'BASEPATH' ) ) exit('No direct script access allowed');class newsletter_model extends CI_Model {    public function get_newsletters() {        $this->db->from("newsletters");        $this->db->order_by("id", "DESC");        return $this->db->get()->result_array();    }    public function get_newsletter( $id ) {        $this->db->from("newsletters");        $this->db->where("id", $id);        return $this->db->get()->row_array();    }    public function get_useri_abonati() {        $this->db->from("useri");        $this->db->where("newsletter", 1);        return $this->db->get()->result_array();    }    public function get_newsletter_abonati() {        $this->db->from("newsletters_abonati");        return $this->db->get()->result_array();    }    public function get_abonat( $id_abonat ) {        $this->db->from("newsletters_abonati");        $this->db->where("id", $id_abonat);        return $this->db->get()->row_array();    }}/* End of file bannere_model.php *//* Location: ./application/models/admin/bannere_model.php */